mapper.select:
  		SELECT COUNT(*)
		FROM parter_order A
		INNER JOIN community B
		ON A.community_id = B.id
		WHERE 
		
        	B.province = #{provinces}
        	AND
      	 
		B.city = #{city}
		AND B.area = #{area}
		
		AND B.community_name = #{country} 
		AND A.effective = #{effective}
