mapper.select:
SELECT
	COUNT(1)
FROM
    ${orderPartner}
WHERE
	partner_id = #{partnerId}
	AND order_date = #{orderDate}
	AND order_status = #{orderStatus}
	AND effective = '1'

SELECT A.id AS id
FROM ${tableName} A
WHERE A.user_id = #{userId} 
AND A.order_date = ${orderDate}
AND A.effective = "1"
